document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll(".sl-share-button").forEach((e=>{e.addEventListener("click",(function(){const e=this.getAttribute("sl-share"),o=window.location.href,t=document.title;let n="";switch(e){case"facebook":n=`https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(o)}`;break;case"whatsapp":n=`https://wa.me/?text=${encodeURIComponent(t+" "+o)}`;break;case"email":n=`mailto:?subject=${encodeURIComponent(t)}&body=${encodeURIComponent(t+" "+o)}`;break;default:return void console.error(`Unsupported share platform: ${e}`)}n&&window.open(n,"_blank","noopener,noreferrer")}))}))}));
