document.addEventListener('DOMContentLoaded',function(){const shareButtons=document.querySelectorAll('.sl-share-button');shareButtons.forEach(button=>{button.addEventListener('click',function(){const platform=this.getAttribute('sl-share');const shareURL=window.location.href;const shareText=document.title;const ogImage=document.querySelector('meta[property="og:image"]')?document.querySelector('meta[property="og:image"]').getAttribute('content'):'';let url='';switch(platform){case 'facebook':url=`https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(shareURL)}`;break;case 'whatsapp':url=`https://wa.me/?text=${encodeURIComponent(shareText + ' ' + shareURL)}`;break;case 'email':url=`mailto:?subject=${encodeURIComponent(shareText)}&body=${encodeURIComponent(shareText + ' ' + shareURL)}`;break;case 'pinterest':if(ogImage){url=`https://pinterest.com/pin/create/button/?url=${encodeURIComponent(shareURL)}&media=${encodeURIComponent(ogImage)}&description=${encodeURIComponent(shareText)}`}else{alert('No Open Graph image found for Pinterest sharing.');return}
break;default:console.error(`Unsupported share platform: ${platform}`);return}
if(url){window.open(url,'_blank','noopener,noreferrer')}})})})
